from typing import List

class Solution:
    def maxScore(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        dp = [[set() for _ in range(n)] for _ in range(m)]

        for i in range(m):
            for j in range(n):
                dp[i][j].add(grid[i][j])

        for i in range(m-1, -1, -1):
            for j in range(n):
                if i < m-1:
                    for k in range(n):
                        if k != j:
                            dp[i][j] |= dp[i+1][k]

        res = 0
        for i in range(n):
            max_val = 0
            for j in range(m):
                max_val = max(max_val, max(dp[j][i]))
            res += max_val

        return res
