    count = 0
    for i in range(H if direction == 'h' else W):
        consecutive_o = 0
        for j in range(W if direction == 'h' else H):
            if direction == 'h':
                if grid[i][j] == 'o':
                    consecutive_o += 1
                else:
                    consecutive_o = 0
            else:
                if grid[j][i] == 'o':
                    consecutive_o += 1
                else:
                    consecutive_o = 0
            if consecutive_o == K:
                count += K - 1
                break
        else:
            count += consecutive_o
    return count

