H, W, K = map(int, input().split())
grid = [input() for _ in range(H)]

def count_consecutive_cells(grid, K):
    # Count consecutive 'o's in rows
    row_counts = [[0] * W for _ in range(H)]
    for i in range(H):
        count = 0
        for j in range(W):
            if grid[i][j] == 'o':
                count += 1
            else:
                count = 0
            row_counts[i][j] = count

    # Count consecutive 'o's in columns
    col_counts = [[0] * H for _ in range(W)]
    for j in range(W):
        count = 0
        for i in range(H):
            if grid[i][j] == 'o':
                count += 1
            else:
                count = 0
            col_counts[j][i] = count

    # Calculate minimum operations required
    min_ops = float('inf')
    for i in range(H):
        for j in range(W - K + 1):
            if all(cell == 'o' for cell in grid[i][j:j+K]):
                min_ops = 0
                break
            ops = K - row_counts[i][j+K-1]
            min_ops = min(min_ops, ops)

    for j in range(W):
