from collections import deque

N, M = map(int, input().split())
X = list(map(int, input().split()))

X_set = set(X)
X_index = {x: i for i, x in enumerate(X)}

dp = [[float('inf')] * (M + 1) for _ in range(N + 1)]
dp[0][0] = 0

q = deque([(0, 0, 0)])  # (pos, prev, length)

while q:
    pos, prev, length = q.popleft()
    if pos == N:
        pos = 0
    for next_pos in [pos - 1, pos + 1]:
        if next_pos < 0:
            next_pos = N - 1
        if next_pos > N - 1:
            next_pos = 0
        if dp[next_pos][prev + (next_pos in X_set)] > length + 1:
            dp[next_pos][prev + (next_pos in X_set)] = length + 1
            q.append((next_pos, prev + (next_pos in X_set), length + 1))

ans = float('inf')
for i in range(N):
    if X_index[X[0]] <= i:
        ans = min(ans, dp[i][M])

print(ans)
