    return n // m - n // (m * n) * (m * n)

def kth_smallest_divisible_by_n_or_m(n, m, k):
    if n > m:
        n, m = m, n
    count_n = count_divisible_by_n_but_not_m(k, n)
    count_m = count_divisible_by_n_but_not_m(k, m)
    if count_n + count_m < k:
        return kth_smallest_divisible_by_n_or_m(m, n, k - count_n - count_m) * n * m // math.gcd(n, m)
    elif count_n >= k:
        return k * n // math.gcd(k, n)
    else:
        return k * m // math.gcd(k, m)

print(kth_smallest_divisible_by_n_or_m(N, M, K))
