from collections import defaultdict, deque
from typing import List, Tuple

def solve() -> None:
    N, M = map(int, input().split())
    trains: List[Tuple[int, int, int, int, int, int]] = []
    for _ in range(M):
        l, d, k, c, A, B = map(int, input().split())
        trains.append((l, d, k, c, A, B))

    graph = defaultdict(list)
    for l, d, k, c, A, B in trains:
        for t in range(l, l + d * (k - 1) + 1, d):
            graph[A].append((t, c, B))

    dp = [-float('inf')] * (N + 1)
    dp[1] = 0
    queue = deque([1])
    while queue:
        node = queue.popleft()
        for t, c, neighbor in graph[node]:
            if dp[node] + t + c > dp[neighbor]:
                dp[neighbor] = dp[node] + t + c
                queue.append(neighbor)

    for i in range(1, N):
        if dp[i] == -float('inf'):
            print("Unreachable")
        else:
            print(dp[i])

if __name__ == "__main__":
