            return True
        if h + a <= H and w - b + 1 <= W:
            return True
        if h - a + 1 <= H and w + b <= W:
            return True
        if h + b <= H and w - a + 1 <= W:
            return True
        if h - b + 1 <= H and w + a <= W:
            return True
        return False

