from collections import deque

H, W = map(int, input().split())
grid = [list(input()) for _ in range(H)]
N = int(input())
medicines = []
for _ in range(N):
    R, C, E = map(int, input().split())
    medicines.append((R-1, C-1, E))

start = None
goal = None
for i in range(H):
    for j in range(W):
        if grid[i][j] == 'S':
            start = (i, j)
        elif grid[i][j] == 'T':
            goal = (i, j)

queue = deque([(start, 0)])
visited = set([(start, 0)])
while queue:
    (x, y), energy = queue.popleft()
    if (x, y) == goal:
        print('Yes')
        exit()
    for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < H and 0 <= ny < W and grid[nx][ny] != '#':
            nenergy = energy - 1
            if nenergy < 0:
                continue
            for mx, my, me in medicines:
                if (nx, ny) == (mx, my):
                    nenergy = me
                    break
            if (nx, ny, nenergy) not in visited:
                queue.append(((nx, ny), nenergy))
                visited.add((nx, ny, nenergy))

print('No')
```

