import sys
from heapq import heapify, heappop
from collections import defaultdict

def main():
    N, M = map(int, sys.stdin.readline().split())
    graph = defaultdict(list)
    parent = list(range(N + 1))

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        x, y = find(x), find(y)
        if x != y:
            parent[x] = y

    for _ in range(M):
        K, C = map(int, sys.stdin.readline().split())
        vertices = list(map(int, sys.stdin.readline().split()))
        for i in range(K):
            for j in range(i + 1, K):
                graph[vertices[i]].append((C, vertices[j]))
                graph[vertices[j]].append((C, vertices[i]))

    edges = []
    for u in range(1, N + 1):
        for C, v in graph[u]:
            edges.append((C, u, v))

    heapify(edges)

    mst_weight = 0
    while edges:
        C, u, v = heappop(edges)
        if find(u) != find(v):
            mst_weight += C
            union(u, v)

    if len(set(find(i) for i in range(1, N + 1))) == 1:
        print(mst_weight)
    else:
        print(-1)

if __name__ == "__main__":
    main()
