prefix_sum = [0] * (1 << N)
def query(i, j):
    print(f"? {i} {j}")
    return int(input())
def update(i, j, val):
    l, r = 2**i * j, 2**i * (j+1) - 1
    while l <= R:
        prefix_sum[l] += val
        l += 2**i
    while r >= L:
        prefix_sum[r] -= val
        r -= 2**i
