from collections import deque

N = int(input())
S = list(input())
T = list(input())

def is_possible(S, T):
    if len(S) != len(T):
        return False
    w_s, w_t, b_s, b_t = 0, 0, 0, 0
    for s, t in zip(S, T):
        if s == 'W':
            w_s += 1
        else:
            b_s += 1
        if t == 'W':
            w_t += 1
        else:
            b_t += 1
    return w_s == w_t and b_s == b_t

def bfs(S, T):
    queue = deque([(S, 0)])
    visited = {tuple(S)}
    while queue:
        state, step = queue.popleft()
        if state == T:
            return step
        for i in range(N - 1):
            if state[i] != '.' and state[i + 1] != '.':
                new_state = list(state)
                new_state[i], new_state[i + 1], new_state[N], new_state[N + 1] = new_state[N], new_state[N + 1], new_state[i], new_state[i + 1]
                new_state_tuple = tuple(new_state)
                if new_state_tuple not in visited:
                    queue.append((new_state, step + 1))
                    visited.add(new_state_tuple)
    return -1

if is_possible(S, T):
    S += ['.', '.']
    print(bfs(S, T))
else:
    print(-1)
