import sys
from heapq import heapify, heappop

def read_input():
    N = int(sys.stdin.readline())
    edges = []
    for _ in range(N - 1):
        A, B, C = map(int, sys.stdin.readline().split())
        edges.append((C, A, B))
    return N, edges

def kruskal(N, edges):
    parent = list(range(N + 1))
    rank = [0] * (N + 1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            if rank[root_x] > rank[root_y]:
                parent[root_y] = root_x
            else:
                parent[root_x] = root_y
                if rank[root_x] == rank[root_y]:
                    rank[root_y] += 1

    heapify(edges)
    mst_cost = 0
    while edges:
        cost, A, B = heappop(edges)
        if find(A) != find(B):
            union(A, B)
            mst_cost += cost
    return mst_cost

N, edges = read_input()
print(kruskal(N, edges))
```
