import heapq
import sys

N, M = map(int, sys.stdin.readline().split())
A = list(map(int, sys.stdin.readline().split()))
edges = []
for _ in range(M):
    u, v, b = map(int, sys.stdin.readline().split())
    edges.append((b, u, v))

edges.sort()
parent = list(range(N + 1))
rank = [0] * (N + 1)

def find(x):
    if parent[x] != x:
        parent[x] = find(parent[x])
    return parent[x]

def union(x, y):
    root_x = find(x)
    root_y = find(y)
    if root_x != root_y:
        if rank[root_x] > rank[root_y]:
            parent[root_y] = root_x
        else:
            parent[root_x] = root_y
            if rank[root_x] == rank[root_y]:
                rank[root_y] += 1

for b, u, v in edges:
    union(u, v)

dist = [float('inf')] * (N + 1)
dist[1] = A[0]
pq = [(A[0], 1)]

while pq:
    d, u = heapq.heappop(pq)
    if d > dist[u]:
        continue
    for v in range(1, N + 1):
        if find(u) == find(v):
            nd = d + A[v - 1]
            if nd < dist[v]:
                dist[v] = nd
                heapq.heappush(pq, (nd, v))

print(' '.join(map(str, dist[2:])))
