        return 0
    take = 0 if x + dishes[i][0] > X or y + dishes[i][1] > Y else 1 + dp(i + 1, x + dishes[i][0], y + dishes[i][1])
    not_take = dp(i + 1, x, y)
    return max(take, not_take)

