@lru_cache(None)
def dp(i, x, y):
    if i == N:
        return 0
    a, b = dishes[i]
    if x + a <= X and y + b <= Y:
        return max(1 + dp(i + 1, x + a, y + b), dp(i + 1, x, y))
    else:
        return dp(i + 1, x, y)

