import sys
from heapq import heapify, heappop

N, Q = map(int, sys.stdin.readline().split())
edges = []
for _ in range(Q):
    L, R, C = map(int, sys.stdin.readline().split())
    for j in range(L, R + 1):
        edges.append((C, N + _, j))

edges.sort()
parent = list(range(N + Q + 1))
rank = [0] * (N + Q + 1)

def find(x):
    if parent[x] != x:
        parent[x] = find(parent[x])
    return parent[x]

def union(x, y):
    root_x, root_y = find(x), find(y)
    if root_x != root_y:
        if rank[root_x] > rank[root_y]:
            parent[root_y] = root_x
        else:
            parent[root_x] = root_y
            if rank[root_x] == rank[root_y]:
                rank[root_y] += 1

mst_cost = 0
heapify(edges)
while edges:
    C, u, v = heappop(edges)
    if find(u) != find(v):
        union(u, v)
        mst_cost += C

connected = True
for i in range(1, N + Q + 1):
    if find(i) != find(1):
        connected = False
        break

if connected:
    print(mst_cost)
else:
    print(-1)
