import sys
from heapq import heapify, heappop

def main():
    N, Q = map(int, sys.stdin.readline().split())
    edges = []
    for i in range(Q):
        L, R, C = map(int, sys.stdin.readline().split())
        for j in range(L, R + 1):
            edges.append((C, N + i + 1, j))
    edges.sort()
    heapify(edges)

    parent = list(range(N + Q + 1))
    rank = [0] * (N + Q + 1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            if rank[root_x] > rank[root_y]:
                parent[root_y] = root_x
            else:
                parent[root_x] = root_y
                if rank[root_x] == rank[root_y]:
                    rank[root_y] += 1

    cost = 0
    connected = True
    while edges:
        c, u, v = heappop(edges)
        if find(u) != find(v):
            union(u, v)
            cost += c
        else:
            connected = False
            break

    if connected:
        print(cost)
    else:
        print(-1)

if __name__ == "__main__":
