def xor_sum(arr, m, k, mod):
    n = len(arr)
    dp = [[0] * (m + 1) for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(1, n + 1):
        for j in range(m + 1):
            dp[i][j] = dp[i - 1][j]
            if j >= 1:
                dp[i][j] += dp[i - 1][(j - 1) % m]
            dp[i][j] %= mod
    res = 0
    for i in range(1, n + 1):
        for j in range(m + 1):
            if i % m == 0:
                res += dp[i][j] * pow(reduce(lambda x, y: x ^ y, arr[:i], 0), k, mod)
            else:
                res += dp[i][j] * 0
            res %= mod
    return res

def reduce(func, arr, init):
    res = init
    for elem in arr:
        res = func(res, elem)
    return res

mod = 998244353
n, m, k = map(int, input().split())
arr = list(map(int, input().split()))
print(xor_sum(arr, m, k, mod))
```
