    for i in range(1, n + 1):
        for j in range(m + 1):
            if i % m == 0:
                res += dp[i][j] * pow(reduce(lambda x, y: x ^ y, arr[:i]), k, mod)
            else:
                res += dp[i][j] * 0
            res %= mod
    return res

def reduce(func, arr):
    res = arr[0]
    for elem in arr[1:]:
        res = func(res, elem)
    return res

mod = 998244353
n, m, k = map(int, input().split())
arr = list(map(int, input().split()))
print(xor_sum(arr, m, k, mod))
