import sys

def dijkstra(graph, start, required_bridges):
    queue = [(0, start, set())]
    distances = {node: float('inf') for node in range(1, N + 1)}
    distances[start] = 0
    while queue:
        dist, node, bridges = heapq.heappop(queue)
        if node == N:
            if bridges == required_bridges:
                return dist
            continue
        for neighbor, bridge, time in graph[node]:
            if bridge not in bridges:
                new_bridges = bridges | {bridge}
                new_dist = dist + time
                if new_dist < distances[neighbor]:
                    distances[neighbor] = new_dist
                    heapq.heappush(queue, (new_dist, neighbor, new_bridges))
    return -1

