def count_good_sequences(N, M, MOD):
    def count_divisors(n):
        count = 0
        for i in range(1, int(n ** 0.5) + 1):
            if n % i == 0:
                if n // i == i:
                    count += 1
                else:
                    count += 2
        return count

    dp = [0] * (N + 1)
    dp[0] = 1
    for i in range(1, N + 1):
        if count_divisors(i) % 3 == 0:
            for j in range(i, N + 1):
                dp[j] += dp[j - i]
                dp[j] %= MOD

    return dp[N] ** M % MOD

