    def sum_of_divisors(n):
        res = 1
        for i in range(2, int(n ** 0.5) + 1):
            cnt = 0
            while n % i == 0:
                n //= i
                cnt += 1
            res *= (i ** (cnt + 1) - 1) // (i - 1)
        if n > 1:
            res *= (n ** 2 - 1) // (n - 1)
