def count_operations(n, q, p, v):
    MOD = 998244353
    dp = [[0, 0] for _ in range(q + 1)]
    dp[0][0] = 1
    for i in range(q):
        for j in range(2):
            if j == 0:
                if max(v[i], *(v[k] for k in range(i) if p[k] <= p[i])) <= v[i]:
                    dp[i + 1][0] += dp[i][j]
                    dp[i + 1][0] %= MOD
                if max(v[i], *(v[k] for k in range(i) if p[k] >= p[i])) <= v[i]:
                    dp[i + 1][1] += dp[i][j]
                    dp[i + 1][1] %= MOD
            else:
                if max(v[i], *(v[k] for k in range(i) if p[k] >= p[i])) <= v[i]:
                    dp[i + 1][1] += dp[i][j]
                    dp[i + 1][1] %= MOD
                if max(v[i], *(v[k] for k in range(i) if p[k] <= p[i])) <= v[i]:
                    dp[i + 1][0] += dp[i][j]
                    dp[i + 1][0] %= MOD
    return sum(dp[-1]) % MOD

