class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        count1, count2 = {}, {}
        for num in nums1:
            count1[num] = count1.get(num, 0) + 1
        for num in nums2:
            count2[num] = count2.get(num, 0) + 1

        s = set()
        for num in count1:
            if num in count2:
                s.add(num)
                count1[num] -= min(count1[num], count2[num])
                count2[num] -= min(count1[num], count2[num])

        for num in count1:
            s.update([num] * count1[num])
        for num in count2:
            s.update([num] * count2[num])

        return len(s)
