class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        count1 = Counter(nums1)
        count2 = Counter(nums2)

        common = count1 & count2
        only1 = count1 - common
        only2 = count2 - common

        return len(set(list(only1) + list(only2)) | set(common.elements()))
