class Solution:
    def maxPartitionsAfterOperations(self, s: str, k: int) -> int:
        n = len(s)
        res = 0
        for i in range(n):
            char_count = {}
            j = i
            while j < n:
                char_count[s[j]] = char_count.get(s[j], 0) + 1
                if len(char_count) > k:
                    break
                j += 1
            res = max(res, j - i)
        return (res + 1) if res > 0 else 1
