            for char in 'abcdefghijklmnopqrstuvwxyz':
                if s[i] != char:
                    new_s = s[:i] + char + s[i + 1:]
                    max_partitions_count = max(max_partitions_count, max_partitions(new_s, k))
        return max_partitions_count
