class Solution:
    def minimumOperationsToMakeEqual(self, x: int, y: int) -> int:
        if x < y:
            x, y = y, x
        res = 0
        while x != y:
            if x % 11 == 0:
                x //= 11
            elif x % 5 == 0:
                x //= 5
            else:
                x -= 1
            res += 1
        return res
