class Solution:
    def findMaximumNumber(self, k: int, x: int) -> int:
        def count_set_bits(n):
            return bin(n).count('1')

        def count_price(n, x):
            count = 0
            binary = bin(n)[2:][::-1]
            for i in range(len(binary)):
                if (i + 1) % x == 0 and binary[i] == '1':
                    count += 1
            return count

        left, right = 1, k
        while left <= right:
            mid = (left + right) // 2
            total_price = sum(count_price(i, x) for i in range(1, mid + 1))
            if total_price <= k:
                left = mid + 1
            else:
                right = mid - 1
        return right
