class Solution:
    def minimumArrayLength(self, nums: List[int]) -> int:
        nums.sort(reverse=True)
        n = len(nums)
        res = n
        for i in range(n):
            while i < n - 1 and nums[i] % nums[i + 1] == 0:
                i += 1
            res = min(res, n - i)
        return res
