class TrieNode:
    def __init__(self):
        self.children = {}
        self.count = 0

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, num):
        node = self.root
        for i in range(20, -1, -1):
            bit = (num >> i) & 1
            if bit not in node.children:
                node.children[bit] = TrieNode()
            node = node.children[bit]
        node.count += 1

    def query(self, num, k):
        node = self.root
        res = 0
        for i in range(20, -1, -1):
            bit = (num >> i) & 1
            flip = (k >> i) & 1
            if flip not in node.children:
                flip = 1 - flip
            res += flip
            node = node.children[flip]
        return res

class Solution:
    def minOperations(self, nums: List[int], k: int) -> int:
        trie = Trie()
        for num in nums:
            trie.insert(num)
        res = float('inf')
        for num in nums:
            res = min(res, trie.query(num, k))
        return res
```

