class Solution:
    def minOperations(self, nums: List[int], k: int) -> int:
        n = len(nums)
        xor_all = 0
        for num in nums:
            xor_all ^= num
        if xor_all == k:
            return 0
        res = float('inf')
        for num in nums:
            new_xor = xor_all ^ num
            cnt = 0
            while new_xor != k:
                cnt += 1
                diff = k ^ new_xor
                if diff == 0:
                    break
                diff ^= diff & -diff
                new_xor ^= diff
            res = min(res, cnt)
        return res
