class Solution:
    def canSortArray(self, nums: List[int]) -> bool:
        count = {}
        for num in nums:
            set_bits = bin(num).count('1')
            if set_bits not in count:
                count[set_bits] = []
            count[set_bits].append(num)

        for key in count:
            if count[key] != sorted(count[key]):
                return False

        sorted_nums = []
        for key in sorted(count):
            sorted_nums.extend(sorted(count[key]))

        return sorted_nums == sorted(nums)
