class Solution:
    def countPrefixSuffixPairs(self, words: List[str]) -> int:
        count = 0
        for i in range(len(words)):
            for j in range(i + 1, len(words)):
                if words[i] != words[j] and words[j].startswith(words[i]) and words[j].endswith(words[i]):
                    count += 1
        return count
