class Solution:
    def sumOfPowers(self, nums: List[int], k: int) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        nums.sort()
        dp = [[0]*k for _ in range(n)]

        for i in range(n):
            dp[i][0] = 0

        for i in range(1, n):
            dp[i][1] = (dp[i-1][0] + abs(nums[i] - nums[i-1])) % MOD

        for j in range(2, k):
            for i in range(j, n):
                dp[i][j] = (dp[i-1][j-1] + min(abs(nums[i] - nums[i-1]), dp[i-1][j-1])) % MOD

        return sum(dp[i][k-1] for i in range(k-1, n)) % MOD
```

