class Solution:
    def sumOfPowers(self, nums: List[int], k: int) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        nums.sort()
        dp = [[0]*(k+1) for _ in range(n+1)]

        for i in range(1, n+1):
            dp[i][1] = sum(abs(nums[j]-nums[i-1]) for j in range(i-1))

        for j in range(2, k+1):
            for i in range(j, n+1):
                dp[i][j] = dp[i-1][j-1] + sum(abs(nums[i-1]-nums[m-1]) for m in range(i-1, i-j, -1))
                dp[i][j] %= MOD

        return dp[n][k]
```

