class Solution:
    def maxPalindromesAfterOperations(self, words: List[str]) -> int:
        count = [{} for _ in range(26)]
        for word in words:
            for char in set(word):
                count[ord(char) - ord('a')][word.count(char)] = count[ord(char) - ord('a')].get(word.count(char), 0) + 1

        res = 0
        for i in range(26):
            for j in range(1, 101):
                res += count[i].get(j, 0) // j * j
                res += min(count[i].get(j, 0) % j, j - 1)

        return res
