class Solution:
    def maxOperations(self, nums: List[int]) -> int:
        count = 0
        while len(nums) >= 2:
            target = nums[0] + nums[1]
            nums = nums[2:]
            temp = []
            while nums:
                if nums[0] + nums[-1] != target:
                    temp.append(nums.pop(0))
                else:
                    nums.pop(0)
                    nums.pop()
                    count += 1
            nums = temp
        return count
