class Solution:
    def isPossibleToSplit(self, nums: List[int]) -> bool:
        from collections import Counter
        count = Counter(nums)
        half = len(nums) // 2
        for i in range(1, half + 1):
            if count[nums[i - 1]] >= i:
                count[nums[i - 1]] -= i
                if all(val >= half - i for val in count.values()):
                    return True
                count[nums[i - 1]] += i
        return False
```

