class Solution:
    def isPossibleToSplit(self, nums: List[int]) -> bool:
        count = {}
        for num in nums:
            if num in count:
                count[num] += 1
            else:
                count[num] = 1

        half_len = len(nums) // 2
        nums1, nums2 = set(), set()

        for num, freq in count.items():
            if freq <= half_len:
                if len(nums1) + freq <= half_len:
                    nums1.update([num] * freq)
                elif len(nums2) + freq <= half_len:
                    nums2.update([num] * freq)
                else:
                    return False
            else:
                if len(nums1) + half_len <= len(nums) // 2:
                    nums1.update([num] * half_len)
                if len(nums2) + half_len <= len(nums) // 2:
                    nums2.update([num] * half_len)

        return len(nums1) == half_len and len(nums2) == half_len
