class Solution:
    def shortestSubstrings(self, arr: List[str]) -> List[str]:
        n = len(arr)
        res = [''] * n
        for i in range(n):
            substrings = set()
            for j in range(n):
                if i != j:
                    for k in range(len(arr[j])):
                        for length in range(1, len(arr[j]) - k + 1):
                            substrings.add(arr[j][k:k + length])
            min_len = float('inf')
            for k in range(len(arr[i])):
                for length in range(1, len(arr[i]) - k + 1):
                    substring = arr[i][k:k + length]
                    if substring not in substrings:
                        if length < min_len:
                            min_len = length
                            res[i] = substring
                        elif length == min_len:
                            res[i] = min(res[i], substring)
        return res
