class Solution:
    def minimumDeletions(self, word: str, k: int) -> int:
        freq = {}
        for char in word:
            if char in freq:
                freq[char] += 1
            else:
                freq[char] = 1

        freq_values = sorted(freq.values())
        n = len(freq_values)

        res = float('inf')
        left, right = 0, n - 1
        while left <= right:
            mid = (left + right) // 2
            if self.check(freq_values, k, mid):
                res = min(res, n - mid)
                right = mid - 1
            else:
                left = mid + 1

        return res

    def check(self, freq_values, k, mid):
        max_freq = freq_values[mid]
        min_freq = freq_values[mid]
        for i in range(mid, len(freq_values)):
            max_freq = max(max_freq, freq_values[i])
            min_freq = min(min_freq, freq_values[i])
            if max_freq - min_freq > k:
                return False
        return True
```

