class Solution:
    def findLatestTime(self, s: str) -> str:
        h, m = s.split(":")
        hh, mm = "", ""

        for c in h:
            if c == "?":
                hh += "1" if hh else "9"
            else:
                hh += c

        for c in m:
            if c == "?":
                mm += "9"
            else:
                mm += c

        if int(hh) > 11:
            hh = "11"

        if len(hh) == 1:
            hh = "0" + hh

        return hh + ":" + mm
