from typing import List
import itertools

class Solution:
    def medianOfUniquenessArray(self, nums: List[int]) -> int:
        uniqueness_array = set()
        for r in range(len(nums) + 1):
            for i in range(len(nums) - r + 1):
                subarray = nums[i:i + r]
                uniqueness_array.add(len(set(subarray)))
        uniqueness_array = sorted(list(uniqueness_array))
        n = len(uniqueness_array)
        if n % 2 == 0:
            return (uniqueness_array[n // 2 - 1] + uniqueness_array[n // 2]) // 2
        else:
            return uniqueness_array[n // 2]
