class Solution:
    def mostFrequentIDs(self, nums: List[int], freq: List[int]) -> List[int]:
        freq_count = {}
        max_freq = 0
        ans = []

        for i in range(len(nums)):
            freq_count[nums[i]] = freq_count.get(nums[i], 0) + freq[i]
            if freq_count[nums[i]] < 0:
                freq_count[nums[i]] = 0
            max_freq = max(max_freq, freq_count[nums[i]])
            ans.append(max_freq if any(freq_count.values()) else 0)

        return ans
