class Solution:
    def sumOfEncryptedInt(self, nums: List[int]) -> int:
        encrypted_sum = 0
        for num in nums:
            max_digit = max(int(digit) for digit in str(num))
            encrypted_num = int(str(max_digit) * len(str(num)))
            encrypted_sum += encrypted_num
        return encrypted_sum
