class Solution:
    def countAlternatingSubarrays(self, nums: List[int]) -> int:
        n = len(nums)
        res = n
        curr = 1
        for i in range(1, n):
            if nums[i] != nums[i-1]:
                curr += 1
                res += curr
            else:
                curr = 1
        return res
