class Solution:
    def stringIndices(self, wordsContainer: List[str], wordsQuery: List[str]) -> List[int]:
        def lcs(a, b):
            i, j = len(a), len(b)
            while i > 0 and j > 0:
                if a[i-1] == b[j-1]:
                    i -= 1
                    j -= 1
                else:
                    break
            return i

        ans = []
        for query in wordsQuery:
            max_lcs = 0
            idx = -1
            min_len = float('inf')
            for i, word in enumerate(wordsContainer):
                lcs_len = lcs(query, word)
                if lcs_len > max_lcs:
                    max_lcs = lcs_len
                    idx = i
                    min_len = len(word)
                elif lcs_len == max_lcs:
                    if len(word) < min_len:
                        idx = i
                        min_len = len(word)
            ans.append(idx)
        return ans
```

