class Solution:
    def minOperationsToMakeMedianK(self, nums: List[int], k: int) -> int:
        nums.sort()
        n = len(nums)
        mid = n // 2
        if n % 2 == 0:
            median = (nums[mid - 1] + nums[mid]) // 2
        else:
            median = nums[mid]

        if median == k:
            return 0

        res = float('inf')
        for i in range(n):
            ops = 0
            for j in range(n):
                if i == j:
                    ops += abs(k - nums[j])
                else:
                    ops += abs(median - nums[j])
            res = min(res, ops)

        return res
```

