class Solution:
    def numberOfRightTriangles(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        row_counts, col_counts = [0] * rows, [0] * cols
        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 1:
                    row_counts[r] += 1
                    col_counts[c] += 1

        triangles = 0
        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 1:
                    triangles += (row_counts[r] - 1) * (col_counts[c] - 1)

        return triangles
